MAN.EXAMPLE:
// first example
x=[1 3 7]; y1=1:3; y2=[4 3 5;6 7 8;9 10 11];
bar(x,y1,'yellow');bar(x,y2);
barhomogenize();
barhomogenize('stacked',1);

// second example
// draw one bar (using bar function) and two polylines (using plot function)  
x=1:10; y=sin(x)/2;
bar(x,y,'red')
x1=1:10;y1=[sin(x);cos(x)]
plot(x1,y1)
// modify the polyline_style type of the second polyline from plot (this polyline becomes a bar)
e=gce(); e2=e.children(2); e2.polyline_style=6;
// homogenisation of the first (from bar function) and second bar (from the modification). 
barhomogenize();
// change the style and the width
barhomogenize('stacked',0.5);
barhomogenize('stacked',1);
