MAN.EXAMPLE:
// first example : draw just oone bar, width=0.5, color='yellow', default x=1:size(y,"x")
y=[1 -3 5];
barh(y,0.5,'yellow');
// second example : default style='grouped'
x=[1 2 5]; y=[1  -5 6;3 -2 7;4  -3 8];
barh(x,y);
// third example : draw 3 bars (columns number of y) style='stacked'.
x=[1 2 5]; y=[1 4 7;2 5 8;3 6  9];
barh(x,y,'stacked');
// fourth example; width=0.2;color='green'; default style='grouped'
x=[1 2 5]; y=[1 4 7;2 5 8;3 6 9];
barh(x,y,0.2,'green');
