MAN.EXAMPLE:
set("figure_style","new") // select entity based graphics
f=get("current_figure") // handle of the current figure

drawlater();
subplot(221);
drawlater();
t=1:10;plot2d(t,t.^2)
subplot(222);
drawlater();
x=0:1:7;plot2d(x,cos(x),2) 
subplot(234);
drawlater();
plot2d(t,cos(t),3);
subplot(235);
drawlater();
plot2d(x,sin(2*x),5); 
subplot(236);
drawlater();
plot2d(t,tan(2*t));  

drawnow(); //draw the current axes
drawnow(f.children([3 4])); // draw the specified axes
drawnow("all"); // draw the current figure
