MAN.EXAMPLE:
// example 1: flip x components along the first dimension
x=[1 2 3 4; 5 6 7 8];
dim=1;
y=flipdim(x,dim)

// example 2: flip x components along the second dimension
dim=2;
y=flipdim(x,dim)

// example 3: flip x components along the third dimension
x=matrix(1:48,[3 2,4,2]);
dim=3;
y=flipdim(x,dim)
