int write_string(char *fname,unsigned long fname_len)
{
    int iRet        = 0;

    //variable info : matrix of string 2 x 3
    int iRows       = 2;
    int iCols       = 3;
    char** pstData  = NULL;

    //data to put in the new variable
    char string0[]  = "sample";
    char string1[]  = "with";
    char string2[]  = "strings";
    char string3[]  = "gateway";
    char string4[]  = "manipulation";
    char string5[]  = "API";

    //alloc new array
    pstData         = (char**)malloc(sizeof(char*) * iRows * iCols);

    //copy data address to the "main" array
    pstData[0]      = string0;
    pstData[1]      = string1;
    pstData[2]      = string2;
    pstData[3]      = string3;
    pstData[4]      = string4;
    pstData[5]      = string5;

    //create the variable
    createMatrixOfString(Rhs + 1, iRows, iCols, pstData);

    //free container
    free(pstData);

    //assign allocated variables to Lhs position
    LhsVar(1)       = Rhs + 1;
    PutLhsVar();
    return 0;
}


a_ref = "sample strings manipulation with gateway API ";
b = [];
a = write_string();
for i=1:size(a,"r")
    for j=1:size(a,"c")
        b = b + a(i,j);
        b = b + " ";
    end
end
if b <> a_ref then error("failed"), end
